import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Typist from 'react-typist';
import './App.css';

const App = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const backendUrl = 'https://your-backend-service.onrender.com'; // Thay bằng URL backend trên Render

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const sendMessage = async (message) => {
    if (!message.trim()) return;

    // Thêm tin nhắn người dùng vào danh sách
    setMessages((prev) => [...prev, { text: message, sender: 'user' }]);
    setInput('');
    setIsTyping(true);

    try {
      // Gửi yêu cầu tới backend
      const response = await axios.post(`${backendUrl}/chat`, {
        message,
        userId: 'user1',
      });

      // Thêm phản hồi từ chatbot
      setMessages((prev) => [...prev, { text: response.data.response, sender: 'bot' }]);
    } catch (error) {
      console.error('Error sending message:', error);
      setMessages((prev) => [
        ...prev,
        { text: 'Đã xảy ra lỗi khi gửi tin nhắn. Vui lòng thử lại!', sender: 'bot' },
      ]);
    } finally {
      setIsTyping(false);
    }
  };

  const handleSuggestionClick = (suggestion) => {
    sendMessage(suggestion);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    sendMessage(input);
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>Chatbot Du Lịch</h1>
      </div>
      <div className="chat-body">
        {messages.map((msg, index) => (
          <div key={index} className={`message ${msg.sender}`}>
            {msg.sender === 'bot' ? (
              <Typist
                className="bot-message"
                avgTypingDelay={50}
                stdTypingDelay={20}
                cursor={{ show: false }}
              >
                {msg.text}
              </Typist>
            ) : (
              <div className="user-message">{msg.text}</div>
            )}
          </div>
        ))}
        {isTyping && (
          <div className="message bot">
            <div className="typing-indicator">
              <span></span>
              <span></span>
              <span></span>
            </div>
          </div>
        )}
        <div ref={messagesEndRef} />
      </div>
      <div className="suggestions">
        <button onClick={() => handleSuggestionClick('Tôi muốn tìm hiểu về văn hóa Khánh Hòa')}>
          Văn hóa
        </button>
        <button onClick={() => handleSuggestionClick('Tôi muốn tìm sự kiện ở Nha Trang')}>
          Sự kiện
        </button>
        <button onClick={() => handleSuggestionClick('Tôi muốn tìm địa điểm tham quan ở Nha Trang')}>
          Địa điểm du lịch
        </button>
        <button onClick={() => handleSuggestionClick('Tôi đang ở Nha Trang, muốn ăn hải sản')}>
          Ẩm thực
        </button>
        <button onClick={() => handleSuggestionClick('Tôi bị ho, tìm bệnh viện ở Nha Trang')}>
          Y tế
        </button>
      </div>
      <form className="chat-input" onSubmit={handleSubmit}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Nhập tin nhắn..."
        />
        <button type="submit">Gửi</button>
      </form>
    </div>
  );
};

export default App;
